let mediaRecorder;
let audioChunks = [];
let isRecording = false;

// Функция для начала записи
Script.Signals.Start = function () {
    if (!isRecording) {
        if (mediaRecorder && mediaRecorder.state === "inactive") {
            mediaRecorder.start();
            isRecording = true;
            Script.Outputs.isRecording = true;
            Script.Outputs.statusMessage = "Запись началась";
        }
    }
};

// Функция для остановки записи
Script.Signals.Stop = function () {
    if (isRecording) {
        if (mediaRecorder && mediaRecorder.state === "recording") {
            mediaRecorder.stop();
            isRecording = false;
            Script.Outputs.isRecording = false;
            Script.Outputs.statusMessage = "Запись остановлена";
        }
    }
};

// Запрашиваем доступ к микрофону
navigator.mediaDevices.getUserMedia({ audio: true })
    .then(stream => {
        mediaRecorder = new MediaRecorder(stream);

        mediaRecorder.ondataavailable = event => {
            audioChunks.push(event.data);
        };

        mediaRecorder.onstop = () => {
            const audioBlob = new Blob(audioChunks, { type: 'audio/wav' });
            audioChunks = [];

            // Преобразуем Blob в Base64
            const reader = new FileReader();
            reader.onloadend = () => {
                const base64Data = reader.result.split(',')[1]; // Убираем префикс "data:audio/wav;base64,"
                Script.Outputs.audioBlob = audioBlob;
                Script.Outputs.audioUrl = URL.createObjectURL(audioBlob);
                Script.Outputs.isRecording = false;
                Script.Outputs.statusMessage = "Запись завершена";

                // Отправляем Base64 на вебхук
                uploadToWebhook(base64Data);
            };
            reader.readAsDataURL(audioBlob); // Читаем Blob как Data URL
        };
    })
    .catch(error => {
        console.error("Error accessing microphone:", error);
        Script.Outputs.error = error.message;
        Script.Outputs.statusMessage = "Ошибка доступа к микрофону";
    });

async function uploadToWebhook(base64Data) {
    // Обновляем статус на "Транскрибируем..."
    Script.Outputs.uploadStatus = "Транскрибируем...";

    // Создаем AbortController для управления таймаутом
    const controller = new AbortController();
    const timeout = 60000; // 60 секунд

    // Устанавливаем таймер для прерывания запроса
    const timeoutId = setTimeout(() => {
        controller.abort(); // Прерываем запрос
    }, timeout);

    try {
        // Отправляем Base64 на вебхук
        const response = await fetch("https://n8n.rgneuro.ru/webhook/noodl-test", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                file: base64Data,
                filename: "recording.wav"
            }),
            signal: controller.signal // Передаем сигнал для управления запросом
        });

        clearTimeout(timeoutId); // Очищаем таймер, если ответ получен

        if (!response.ok) {
            throw new Error(`Ошибка HTTP: ${response.status}`);
        }

        const text = await response.text(); // Читаем ответ как текст
        console.log("Ответ от вебхука (текст):", text); // Логируем ответ

        // Проверяем, есть ли текст в ответе
        if (text && text.trim() !== "") {
            Script.Outputs.transcription = text; // Используем текст как транскрибацию
            Script.Outputs.uploadStatus = "Транскрибация получена";
        } else {
            throw new Error("Транскрибация пустая");
        }
    } catch (error) {
        clearTimeout(timeoutId); // Очищаем таймер в случае ошибки
        console.error("Ошибка при отправке файла:", error);

        // Разделяем ошибки
        if (error.name === "AbortError") {
            Script.Outputs.uploadStatus = "Таймаут: сервер не ответил";
        } else if (error.message === "Транскрибация пустая") {
            Script.Outputs.uploadStatus = "Транскрибация пустая";
        } else {
            Script.Outputs.uploadStatus = "Ошибка отправки";
        }

        Script.Outputs.transcription = "";
    }
}

